INSERT INTO ArtPieces (Title, ArtistID, CreatedAt, Description, ImageURL, Category) VALUES ('Poet Paying Homage to Krishna, folio from a Bhagavata Purana Series', 2, 'c. 1820', 'The painting depicts Krishna, the eight avatar of the Hindu god Vishnu who is worshipped as a deity in his own right, enthroned on an enclosed, outdoor terrace. He holds in his hand a long white scroll. Kneeling in front of him is a poet with raised hands. An attendant dressed in an orange robe (jama) stands on the left.', 'https://nrs.harvard.edu/urn-3:HUAM:ISL10190_dynmc', 'Digital');
