INSERT INTO ArtPieces (Title, ArtistID, CreatedAt, Description, ImageURL, Category) VALUES ('Painted Panel from Wooden Sarcophagus', 2, '2nd century CE', 'Wooden panel over a foot in width, covered with gesso, with a figured scene of two seated deities, each facing a smaller standing figure.  The upper border is stylized cobras, and there are sections of painted hieroglyphic writing.', 'https://nrs.harvard.edu/urn-3:HUAM:INV027712_dynmc', 'Painting');
